public class CustomInterceptor extends TransactionInterceptor { private static final long serialVersionUID = 1154144110124764905L; public CustomInterceptor(PlatformTransactionManager ptm, TransactionAttributeSource tas) { super(ptm, tas); } @Override public Object invoke(MethodInvocation invocation) throws Throwable { before(invocation.getMethod()); Object invoke = null; try { invoke = super.invoke(invocation); } finally { after(); } return invoke; } public void before(Method method) { // 这里都拿到method对象了，那通过反射可以做的事情就很多了，能到这里来的，那方法上面肯定是有Transactional注解的，拿到它并获取相关属性，如果事务属性为只读的，那毫无疑问可以把它对数据的请求打到从库 Transactional transactional = method.getAnnotation(Transactional.class); boolean readOnly = transactional.readOnly(); if (readOnly) { // 只读事务，切换到mysql的从库 changeDatasource(DatasourceType.SLAVE); } else { // 非只读事务，切换到mysql主库 changeDatasource(DatasourceType.MASTER); } } public void after() { // 清除数据源设置 changeDatasource(DatasourceType.CLEAN); } private void changeDatasource(DatasourceType type) { //模拟数据源切换 System.out.println("


==========================================================="); System.out.println("Datasource = " + type); System.out.println("===========================================================


"); } } enum DatasourceType { MASTER, SLAVE, CLEAN }
